"use strict";
var Complex = require('Complex'); //exportable class
var rngOpsRisk = function(f1, f2, y10, y20, options) {
  var n=options.n;
  var maxX=options.maxX;
  var h=(maxX)/n;
  var beta=[];
  var alpha=[];
  beta.push(y10);
  alpha.push(y20);
  var cmplH=new Complex(h, 0);
  for(var i=0; i<n; i++){
    beta[i+1]=beta[i].add(f1(beta[i]).multiply(cmplH));
    alpha[i+1]=alpha[i].add(f2(beta[i]).multiply(cmplH));
  }
  return [beta, alpha];
}

module.exports=rngOpsRisk; //exportable class
